import React, { useEffect, useRef, useState } from 'react'
import { Helmet } from 'react-helmet'

/* ==== Icons & button style ==== */
const IconCopy = (props) => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" {...props}>
    <rect x="9" y="9" width="11" height="11" rx="2" stroke="currentColor" strokeWidth="1.7"/>
    <rect x="4" y="4" width="11" height="11" rx="2" stroke="currentColor" strokeWidth="1.7"/>
  </svg>
)

const IconThumbUp = (props) => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" {...props}>
    <path d="M14 10V5a2 2 0 0 0-2-2l-3 7H5a2 2 0 0 0-2 2v7h10.3a3 3 0 0 0 2.9-2.1l2.1-6.2A2 2 0 0 0 16.4 9H14z" stroke="currentColor" strokeWidth="1.7" strokeLinejoin="round"/>
  </svg>
)

const IconThumbDown = (props) => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" {...props}>
    <path d="M10 14v5a2 2 0 0 0 2 2l3-7h4a2 2 0 0 0 2-2V5H10.7a3 3 0 0 0-2.9 2.1L5.7 13.3A2 2 0 0 0 7.6 16H10z" stroke="currentColor" strokeWidth="1.7" strokeLinejoin="round"/>
  </svg>
)

/*  active: nền đen, chữ trắng, border đen  */
const actionBtnStyle = (active=false) => ({
  background: active ? '#111827' : '#fff',
  border: `1px solid ${active ? '#111827' : '#e5e7eb'}`,
  color: active ? '#ffffff' : '#6b7280',
  padding: '6px 10px',
  borderRadius: 10,
  display: 'inline-flex',
  alignItems: 'center',
  gap: 6,
  cursor: 'pointer',
  boxShadow: active ? '0 2px 10px rgba(0,0,0,0.15)' : '0 1px 4px rgba(0,0,0,0.04)',
  userSelect: 'none',
  transition: 'all .15s ease'
})

/* ==== Avatar ==== */
const Avatar = () => (
  <div style={{ width: 64, height: 64, borderRadius: 12, background: '#e5e7eb', display: 'grid', placeItems: 'center' }}>
    <span style={{ fontWeight: 700 }}>E</span>
  </div>
)

/* ==== Message Bubble ==== */
const MessageBubble = ({ role, children, media }) => {
  const isUser = role === 'user'
  return (
    <div style={{ display: 'grid', justifyItems: isUser ? 'end' : 'start' }}>
      <div style={{
        maxWidth: 720,
        background: isUser ? '#111827' : '#ffffff',
        color: isUser ? 'white' : '#111827',
        border: isUser ? 'none' : '1px solid #e5e7eb',
        padding: media ? 8 : 12,
        borderRadius: 16,
        boxShadow: isUser ? '0 4px 14px rgba(0,0,0,0.15)' : '0 4px 14px rgba(0,0,0,0.06)',
        display: 'inline-block',
        width: 'fit-content',
        lineHeight: 1.6,
      }}>
        {media ? (
          <img src={media} alt="attachment" style={{ width: 260, borderRadius: 12 }} />
        ) : (
          <div style={{ whiteSpace: 'pre-wrap' }}>{children}</div>
        )}
      </div>
    </div>
  )
}
// Đặt trên cùng file, trước ChatPage
const AutoTextarea = React.forwardRef(function AutoTextarea(
  {
    value,
    onChange,
    onSend,              // gọi khi Enter (không kèm Ctrl/Cmd)
    placeholder,
    style
  },
  ref
) {
  const localRef = useRef(null)
  const textareaRef = ref || localRef

  const autoSize = () => {
    const el = textareaRef.current
    if (!el) return
    el.style.height = 'auto'
    // Giới hạn chiều cao tối đa để không chiếm màn hình
    const max = 240 // px (tuỳ bạn)
    const h = Math.min(el.scrollHeight, max)
    el.style.height = h + 'px'
    el.style.overflowY = el.scrollHeight > max ? 'auto' : 'hidden'
  }

  useEffect(() => { autoSize() }, [value])

  const onKeyDown = (e) => {
    if (e.key === 'Enter') {
      if (e.metaKey || e.ctrlKey) {
        // chèn \n tại caret và không gửi
        e.preventDefault()
        const el = textareaRef.current
        if (!el) return
        const start = el.selectionStart
        const end = el.selectionEnd
        const next = value.slice(0, start) + '\n' + value.slice(end)
        onChange({ target: { value: next } })
        // đặt lại caret sau \n
        requestAnimationFrame(() => {
          el.selectionStart = el.selectionEnd = start + 1
          autoSize()
        })
      } else {
        // Enter thường => gửi
        e.preventDefault()
        onSend?.()
      }
    }
  }

  return (
    <textarea
      ref={textareaRef}
      value={value}
      onChange={(e) => { onChange(e); /* autosize lúc gõ */ requestAnimationFrame(autoSize) }}
      onKeyDown={onKeyDown}
      placeholder={placeholder}
      rows={1}
      style={{
        flex: 1,
        background: 'transparent',
        color: '#111827',
        border: 'none',
        outline: 'none',
        padding: '12px 10px',
        fontSize: 18,
        lineHeight: 1.6,
        resize: 'none',
        overflow: 'hidden',
        whiteSpace: 'pre-wrap',
        wordBreak: 'break-word',
        ...style,
      }}
      aria-label="Message input"
    />
  )
})

/* ==== Small toast ==== */
const Toast = ({ show, children }) => (
  <div
    aria-live="polite"
    style={{
      position: 'absolute',
      top: -100,
      left: '50%',
      transform: 'translateX(-50%)',
      opacity: show ? 1 : 0,
      transition: 'opacity .2s ease',
      pointerEvents: 'none'
    }}
  >
    <div style={{
      background: '#111827', color: 'white',
      padding: '6px 10px', borderRadius: 999,
      fontSize: 12, boxShadow: '0 6px 18px rgba(0,0,0,.18)'
    }}>
      {children}
    </div>
  </div>
)

/* ==== Main Page ==== */
const ChatPage = () => {
  const [messages, setMessages] = useState([])
  const [text, setText] = useState('')
  const [mediaPreview, setMediaPreview] = useState(null)
  const [mediaFile, setMediaFile] = useState(null)
  const [recording, setRecording] = useState(false)

  const listRef = useRef(null)
  const bottomRef = useRef(null)

  const barRef = useRef(null)       // wrapper fixed
  const barInnerRef = useRef(null)  // inner box được đo bằng ResizeObserver
  const [bottomPad, setBottomPad] = useState(96);

  // ratings & copy states
  const [ratings, setRatings] = useState({})
  const [copiedId, setCopiedId] = useState(null)

  // Auto scroll khi có tin nhắn mới hoặc khi padding đáy thay đổi
  useEffect(() => {
    if (!barRef.current) return;

    const update = () => {
      const h = barRef.current?.offsetHeight ?? 0;
      setBottomPad(h + 16); // +16px breathing
    };

    // đo động khi bar thay đổi kích thước
    const ro = new ResizeObserver(update);
    ro.observe(barRef.current);

    // nghe luôn sự kiện resize (desktop)
    window.addEventListener('resize', update);
    update();

    return () => {
      ro.disconnect();
      window.removeEventListener('resize', update);
    };
  }, []);

  // Đo chiều cao thực của thanh input (kể cả margin) + safe-area
  useEffect(() => {
    const el = barInnerRef.current
    if (!el) return

    const compute = () => {
      const rect = el.getBoundingClientRect()
      const cs = window.getComputedStyle(el)
      const margin = parseFloat(cs.marginTop) + parseFloat(cs.marginBottom)
      const safe = Number(getComputedStyle(document.documentElement)
        .getPropertyValue('--sat-safe-area-bottom') || 0)
      setBottomPad(rect.height + margin + 8 + safe)
    }

    // fall back: đọc env(safe-area-inset-bottom) qua CSS var
    const updateCSSVar = () => {
      const div = document.createElement('div')
      div.style.cssText = 'position:fixed;bottom:0;left:0;right:0;padding-bottom:env(safe-area-inset-bottom);visibility:hidden;'
      document.body.appendChild(div)
      const pb = parseFloat(getComputedStyle(div).paddingBottom) || 0
      document.documentElement.style.setProperty('--sat-safe-area-bottom', pb)
      document.body.removeChild(div)
    }

    updateCSSVar()
    compute()
    const ro = new ResizeObserver(compute)
    ro.observe(el)
    window.addEventListener('resize', compute)

    return () => {
      ro.disconnect()
      window.removeEventListener('resize', compute)
    }
  }, [])

  const handleRate = (id, val) => {
    setRatings(prev => ({ ...prev, [id]: prev[id] === val ? undefined : val }))
  }

  const handleCopy = async (id, text) => {
    if (!text) return
    try {
      await navigator.clipboard.writeText(text)
      setCopiedId(id)
      setTimeout(() => setCopiedId(null), 1200)
    } catch {}
  }

  const sendMessage = async () => {
    if (!text && !mediaPreview) return
    const now = Date.now()
    const newMsgs = []
    if (mediaPreview) newMsgs.push({ id: now, role: 'user', media: mediaPreview })
    if (text) newMsgs.push({ id: now + 1, role: 'user', text })
    setMessages((m) => m.concat(newMsgs))
    setText('')
    setMediaPreview(null)
    setMediaFile(null)
    try {
      const res = await fetch('/api/v1/analysis/analyze-sentiment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ text: text || '[media]' }),
      })
      const j = await res.json()
      const reply = j?.data?.label
        ? `Chào bạn! Hình như chúng ta đã chào nhau rồi nhỉ. Bạn muốn hỏi thêm gì về Emostagram? (Sentiment: ${j.data.label})`
        : 'Chào bạn! Hình như chúng ta đã chào nhau rồi nhỉ. Bạn muốn hỏi thêm gì về Emostagram?'
      setMessages((m) => m.concat({ id: Date.now() + 2, role: 'assistant', text: reply }))
    } catch {
      setMessages((m) => m.concat({ id: Date.now() + 2, role: 'assistant', text: 'Sorry, something went wrong.' }))
    }
  }

  // Voice input
  const startVoice = () => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Voice input not supported in this browser')
      return
    }
    const SR = window.SpeechRecognition || window.webkitSpeechRecognition
    const rec = new SR()
    rec.lang = 'vi-VN'
    rec.interimResults = false
    rec.maxAlternatives = 1
    setRecording(true)
    rec.onresult = (e) => {
      const t = e.results[0][0].transcript
      setText((prev) => (prev ? prev + ' ' + t : t))
    }
    rec.onend = () => setRecording(false)
    rec.onerror = () => setRecording(false)
    rec.start()
  }

  const onPickMedia = (e) => {
    const f = e.target.files?.[0]
    if (!f) return
    const url = URL.createObjectURL(f)
    setMediaPreview(url)
    setMediaFile(f)
  }

  const showHero = messages.length === 0

  return (
    <main style={{ minHeight: '100vh', background: '#f8fafc', color: '#111827' }}>
      <Helmet><title>Chat - Emostagram</title></Helmet>
      <div style={{ maxWidth: 980, margin: '0 auto', padding: '16px 16px 0 16px', display: 'grid', gap: 16 }}>
        {showHero ? (
          <div style={{ minHeight: '70vh', display: 'grid', alignContent: 'center', justifyItems: 'center', gap: 20 }}>
            <Avatar />
            <h1 style={{ marginTop: 8, fontSize: 36, fontWeight: 700 }}>Emostagram</h1>
            <div style={{ width: '100%', display: 'grid', justifyItems: 'center', gap: 12 }}>
              {mediaPreview && (
                <img src={mediaPreview} alt="preview" style={{ width: 72, height: 72, objectFit: 'cover', borderRadius: 14, boxShadow: '0 8px 26px rgba(0,0,0,0.12)' }} />
              )}
              <div style={{
                width: 'min(92vw, 760px)',
                height: 64,
                background: '#ffffff',
                border: '1px solid #e5e7eb',
                borderRadius: 18,
                boxShadow: '0 12px 40px rgba(0,0,0,0.06)',
                display: 'flex',
                alignItems: 'center',
                padding: '0 10px',
                gap: 8
              }}>
                <label style={{ cursor: 'pointer', padding: 8, display: 'grid', placeItems: 'center' }}>
                  📎
                  <input accept="*/*" type="file" style={{ display: 'none' }} onChange={onPickMedia} />
                </label>
                <input
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  onKeyDown={(e) => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); sendMessage() } }}
                  placeholder="What do you want to know?"
                  style={{ flex: 1, background: 'transparent', color: '#111827', border: 'none', outline: 'none', padding: '12px 10px', fontSize: 18 }}
                />
                <button onClick={startVoice} aria-label="voice" style={{ width: 42, height: 42, borderRadius: 999, background: '#111827', color: 'white', border: 'none', display: 'grid', placeItems: 'center' }}>
                  {recording ? '🎙️' : '🎤'}
                </button>
              </div>
            </div>
          </div>
        ) : (
          <>
            {/* List messages: chừa chỗ bằng paddingBottom */}
            <div
              ref={listRef}
              style={{
                display: 'grid',
                gap: 6,
                padding: '0 8px',
                alignItems: 'start',
                gridAutoRows: 'max-content',
                margin: 0,
                // paddingBottom: bottomPad
                paddingBottom: `calc(${bottomPad}px + env(safe-area-inset-bottom, 0px))`,

              }}
            >
              {messages.map((m) => {
                const rated = ratings[m.id]
                return (
                  <div key={m.id} style={{ display: 'grid', gap: 6 }}>
                    <MessageBubble role={m.role} media={m.media}>{m.text}</MessageBubble>

                    {m.role === 'assistant' && (
                      <div style={{ position: 'relative' }}>
                        <Toast show={copiedId === m.id}>Copy thành công</Toast>

                        <div style={{ display: 'flex', gap: 8 }}>
                          <button
                            aria-label="copy"
                            onClick={() => handleCopy(m.id, m.text)}
                            style={actionBtnStyle(copiedId === m.id)}
                          >
                            <IconCopy />
                          </button>

                          {(rated === undefined || rated === 'up') && (
                            <button
                              aria-label="like"
                              onClick={() => handleRate(m.id, 'up')}
                              style={actionBtnStyle(rated === 'up')}
                              title="Like"
                            >
                              <IconThumbUp />
                            </button>
                          )}

                          {(rated === undefined || rated === 'down') && (
                            <button
                              aria-label="dislike"
                              onClick={() => handleRate(m.id, 'down')}
                              style={actionBtnStyle(rated === 'down')}
                              title="Dislike"
                            >
                              <IconThumbDown />
                            </button>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                )
              })}
              {/* sentinel siêu nhỏ để scrollIntoView */}
              <div ref={bottomRef} style={{ height: 1 }} />
            </div>

            {/* Bottom input bar */}
            <div
              ref={barRef}
              style={{
                position: 'fixed',
                left: 0,
                right: 0,
                bottom: 0,
                paddingBottom: 'env(safe-area-inset-bottom)'
              }}
            >
              <div
                ref={barInnerRef}
                style={{
                  maxWidth: 980,
                  margin: '12px auto',
                  position: 'relative'
                }}
              >
                {mediaPreview && (
                  <img
                    src={mediaPreview}
                    alt="preview"
                    style={{
                      position: 'absolute',
                      bottom: '100%',
                      left: 0,
                      width: 56,
                      height: 56,
                      objectFit: 'cover',
                      borderRadius: 12,
                      boxShadow: '0 6px 20px rgba(0,0,0,0.12)',
                      marginBottom: 8
                    }}
                  />
                )}
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: 8,
                  background: '#ffffff',
                  border: '1px solid #e5e7eb',
                  borderRadius: 16,
                  padding: 8
                }}>
                  <label style={{ cursor: 'pointer', padding: 8 }}>
                    📎
                    <input accept="*/*" type="file" style={{ display: 'none' }} onChange={onPickMedia} />
                  </label>
                  <input
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    onKeyDown={(e) => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); sendMessage() } }}
                    placeholder="What do you want to know?"
                    style={{ flex: 1, background: 'transparent', color: '#111827', border: 'none', outline: 'none', padding: '12px 10px', fontSize: 18 }}
                  />
                  <button onClick={startVoice} aria-label="voice" style={{ width: 42, height: 42, borderRadius: 999, background: '#111827', color: 'white', border: 'none', display: 'grid', placeItems: 'center' }}>
                    {recording ? '🎙️' : '🎤'}
                  </button>
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </main>
  )
}

export default ChatPage
