const useFetch = (url) => {
  const [data, setData] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  useEffect(() => {
    let mounted = true
    setLoading(true)
    fetch(url)
      .then((r) => r.json())
      .then((j) => mounted && (setData(j), setLoading(false)))
      .catch((e) => mounted && (setError(e), setLoading(false)))
    return () => { mounted = false }
  }, [url])
  return { data, loading, error }
}

const useInfiniteFeed = (tab) => {
  const [items, setItems] = useState([])
  const [cursor, setCursor] = useState(null)
  const [loading, setLoading] = useState(false)
  const [done, setDone] = useState(false)

  const load = () => {
    if (loading || done) return
    setLoading(true)
    const qs = new URLSearchParams({ limit: '10' })
    if (cursor) qs.set('cursor', cursor)
    fetch(`/api/v1/blogs?${qs.toString()}`)
      .then((r) => r.json())
      .then((j) => {
        setItems((prev) => prev.concat(j.items || []))
        if (j.nextCursor) setCursor(j.nextCursor)
        else setDone(true)
      })
      .finally(() => setLoading(false))
  }

  useEffect(() => {
    // reset when tab changes (future-proof)
    setItems([])
    setCursor(null)
    setDone(false)
  }, [tab])

  return { items, loading, done, load }
}

const BlogsPage = () => {
