import React, { useEffect, useRef, useState } from 'react'
import { Helmet } from 'react-helmet'

const Avatar = () => (
  <div style={{ width: 64, height: 64, borderRadius: 12, background: '#e5e7eb', display: 'grid', placeItems: 'center' }}>
    <span style={{ fontWeight: 700 }}>E</span>
  </div>
)

const MessageBubble = ({ role, children, media }) => {
  const isUser = role === 'user'
  return (
    <div style={{ display: 'flex', justifyContent: isUser ? 'flex-end' : 'flex-start' }}>
      <div style={{
        maxWidth: 760,
        background: isUser ? '#2563EB' : '#ffffff',
        color: isUser ? 'white' : '#111827',
        border: isUser ? 'none' : '1px solid #e5e7eb',
        padding: 12,
        borderRadius: 16,
        borderTopRightRadius: isUser ? 4 : 16,
        borderTopLeftRadius: isUser ? 16 : 4,
      }}>
        {media && (
          <img src={media} alt="attachment" style={{ width: 220, borderRadius: 12, marginBottom: 8 }} />
        )}
        <div>{children}</div>
        {!isUser && (
          <div style={{ marginTop: 8, display: 'flex', gap: 8 }}>
            <button aria-label="like" style={{ background: 'transparent', border: '1px solid #e5e7eb', color: '#374151', padding: '4px 8px', borderRadius: 8 }}>üëç</button>
            <button aria-label="unlike" style={{ background: 'transparent', border: '1px solid #e5e7eb', color: '#374151', padding: '4px 8px', borderRadius: 8 }}>üëé</button>
          </div>
        )}
      </div>
    </div>
  )
}

const ChatPage = () => {
  const [messages, setMessages] = useState([])
  const [text, setText] = useState('')
  const [mediaPreview, setMediaPreview] = useState(null)
  const [mediaFile, setMediaFile] = useState(null)
  const [recording, setRecording] = useState(false)
  const listRef = useRef(null)

  useEffect(() => {
    listRef.current?.scrollTo({ top: listRef.current.scrollHeight, behavior: 'smooth' })
  }, [messages])

  const sendMessage = async () => {
    if (!text && !mediaPreview) return
    const userMsg = { id: Date.now(), role: 'user', text, media: mediaPreview }
    setMessages((m) => m.concat(userMsg))
    setText('')
    setMediaPreview(null)
    setMediaFile(null)
    // Call backend (mock using analysis sentiment for now)
    try {
      const res = await fetch('/api/v1/analysis/analyze-sentiment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ text: userMsg.text || '[media]' }),
      })
      const j = await res.json()
      const reply = j?.data?.label ? `Sentiment: ${j.data.label}` : 'Okay!'
      setMessages((m) => m.concat({ id: Date.now() + 1, role: 'assistant', text: reply }))
    } catch {
      setMessages((m) => m.concat({ id: Date.now() + 1, role: 'assistant', text: 'Sorry, something went wrong.' }))
    }
  }

  // Audio recording ‚Üí transcribe (use Web Speech API if available)
  const startVoice = () => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Voice input not supported in this browser')
      return
    }
    const SR = window.SpeechRecognition || window.webkitSpeechRecognition
    const rec = new SR()
    rec.lang = 'vi-VN'
    rec.interimResults = false
    rec.maxAlternatives = 1
    setRecording(true)
    rec.onresult = (e) => {
      const t = e.results[0][0].transcript
      setText((prev) => (prev ? prev + ' ' + t : t))
    }
    rec.onend = () => setRecording(false)
    rec.onerror = () => setRecording(false)
    rec.start()
  }

  const onPickMedia = (e) => {
    const f = e.target.files?.[0]
    if (!f) return
    const url = URL.createObjectURL(f)
    setMediaPreview(url)
    setMediaFile(f)
  }

  const showHero = messages.length === 0

  return (
    <main style={{ minHeight: '100vh', background: '#f8fafc', color: '#111827' }}>
      <Helmet><title>Chat - Emostagram</title></Helmet>
      <div style={{ maxWidth: 980, margin: '0 auto', padding: '32px 16px', display: 'grid', gap: 24 }}>
        {showHero && (
          <div style={{ display: 'grid', placeItems: 'center', paddingTop: 60, paddingBottom: 24 }}>
            <Avatar />
            <h1 style={{ marginTop: 12 }}>Emostagram</h1>
          </div>
        )}

        <div ref={listRef} style={{ display: 'grid', gap: 12, minHeight: 200, maxHeight: '60vh', overflowY: 'auto', padding: '0 8px' }}>
          {messages.map((m) => (
            <MessageBubble key={m.id} role={m.role} media={m.media}>{m.text}</MessageBubble>
          ))}
        </div>

        <div style={{ position: 'sticky', bottom: 16 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 8, background: '#ffffff', border: '1px solid #e5e7eb', borderRadius: 16, padding: 8 }}>
            <label style={{ cursor: 'pointer', padding: 8 }}>
              üìé
              <input accept="*/*" type="file" style={{ display: 'none' }} onChange={onPickMedia} />
            </label>
            <input
              value={text}
              onChange={(e) => setText(e.target.value)}
              onKeyDown={(e) => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); sendMessage() } }}
              placeholder="What do you want to know?"
              style={{ flex: 1, background: 'transparent', color: '#111827', border: 'none', outline: 'none', padding: '10px 8px' }}
            />
            <button onClick={startVoice} aria-label="voice" style={{ background: '#0ea5e9', border: 'none', color: 'white', padding: '10px 12px', borderRadius: 12 }}>{recording ? 'üéôÔ∏è...' : 'üéôÔ∏è'}</button>
            <button onClick={sendMessage} aria-label="send" style={{ display: 'none' }} />
          </div>
          {mediaPreview && (
            <div style={{ marginTop: 8 }}>
              <img src={mediaPreview} alt="preview" style={{ width: 180, borderRadius: 12 }} />
            </div>
          )}
        </div>
      </div>
    </main>
  )
}

export default ChatPage
