import React, { useEffect, useRef, useState } from 'react'
import { Helmet } from 'react-helmet'

const Avatar = () => (
  <div style={{ width: 64, height: 64, borderRadius: 12, background: '#e5e7eb', display: 'grid', placeItems: 'center' }}>
    <span style={{ fontWeight: 700 }}>E</span>
  </div>
)

const MessageBubble = ({ role, children, media }) => {
  const isUser = role === 'user'
  return (
    <div style={{ display: 'grid', justifyItems: isUser ? 'end' : 'start' }}>
      <div style={{
        maxWidth: 720,
        background: isUser ? '#2563EB' : '#ffffff',
        color: isUser ? 'white' : '#111827',
        border: isUser ? 'none' : '1px solid #e5e7eb',
        padding: media ? 8 : 12,
        borderRadius: 16,
        boxShadow: isUser ? '0 4px 14px rgba(37,99,235,0.15)' : '0 4px 14px rgba(0,0,0,0.06)',
        display: 'inline-block',
        width: 'fit-content',
        lineHeight: 1.6,
      }}>
        {media ? (
          <img src={media} alt="attachment" style={{ width: 260, borderRadius: 12 }} />
        ) : (
          <div style={{ whiteSpace: 'pre-wrap' }}>{children}</div>
        )}
      </div>
    </div>
  )
}

const ChatPage = () => {
  const [messages, setMessages] = useState([])
  const [text, setText] = useState('')
  const [mediaPreview, setMediaPreview] = useState(null)
  const [mediaFile, setMediaFile] = useState(null)
  const [recording, setRecording] = useState(false)
  const listRef = useRef(null)
  const bottomRef = useRef(null)

  useEffect(() => {
    // Always scroll viewport to the latest message
    bottomRef.current?.scrollIntoView({ behavior: 'smooth', block: 'end' })
  }, [messages])

  const sendMessage = async () => {
    if (!text && !mediaPreview) return
    const now = Date.now()
    const newMsgs = []
    if (mediaPreview) newMsgs.push({ id: now, role: 'user', media: mediaPreview })
    if (text) newMsgs.push({ id: now + 1, role: 'user', text })
    setMessages((m) => m.concat(newMsgs))
    setText('')
    setMediaPreview(null)
    setMediaFile(null)
    // Call backend (mock using analysis sentiment for now)
    try {
      const res = await fetch('/api/v1/analysis/analyze-sentiment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ text: text || '[media]' }),
      })
      const j = await res.json()
      const reply = j?.data?.label ? `Chào bạn! Hình như chúng ta đã chào nhau rồi nhỉ. Bạn muốn hỏi thêm gì về Emostagram? (Sentiment: ${j.data.label})` : 'Chào bạn! Hình như chúng ta đã chào nhau rồi nhỉ. Bạn muốn hỏi thêm gì về Emostagram?'
      setMessages((m) => m.concat({ id: Date.now() + 2, role: 'assistant', text: reply }))
    } catch {
      setMessages((m) => m.concat({ id: Date.now() + 2, role: 'assistant', text: 'Sorry, something went wrong.' }))
    }
  }

  // Audio recording → transcribe (use Web Speech API if available)
  const startVoice = () => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Voice input not supported in this browser')
      return
    }
    const SR = window.SpeechRecognition || window.webkitSpeechRecognition
    const rec = new SR()
    rec.lang = 'vi-VN'
    rec.interimResults = false
    rec.maxAlternatives = 1
    setRecording(true)
    rec.onresult = (e) => {
      const t = e.results[0][0].transcript
      setText((prev) => (prev ? prev + ' ' + t : t))
    }
    rec.onend = () => setRecording(false)
    rec.onerror = () => setRecording(false)
    rec.start()
  }

  const onPickMedia = (e) => {
    const f = e.target.files?.[0]
    if (!f) return
    const url = URL.createObjectURL(f)
    setMediaPreview(url)
    setMediaFile(f)
  }

  const showHero = messages.length === 0

  return (
    <main style={{ minHeight: '100vh', background: '#f8fafc', color: '#111827' }}>
      <Helmet><title>Chat - Emostagram</title></Helmet>
      <div style={{ maxWidth: 980, margin: '0 auto', padding: '16px 16px 0 16px', display: 'grid', gap: 16 }}>
        {showHero ? (
          <div style={{ minHeight: '70vh', display: 'grid', alignContent: 'center', justifyItems: 'center', gap: 16 }}>
            <Avatar />
            <h1 style={{ marginTop: 8 }}>Emostagram</h1>
            <div style={{ width: '100%', maxWidth: 860, display: 'grid', justifyItems: 'center', gap: 8 }}>
              {mediaPreview && (
                <img src={mediaPreview} alt="preview" style={{ width: 64, height: 64, objectFit: 'cover', borderRadius: 12, boxShadow: '0 6px 20px rgba(0,0,0,0.12)' }} />
              )}
              <div style={{ display: 'flex', alignItems: 'center', gap: 8, background: '#ffffff', border: '1px solid #e5e7eb', borderRadius: 16, padding: 8 }}>
                <label style={{ cursor: 'pointer', padding: 8 }}>
                  📎
                  <input accept="*/*" type="file" style={{ display: 'none' }} onChange={onPickMedia} />
                </label>
                <input
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  onKeyDown={(e) => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); sendMessage() } }}
                  placeholder="What do you want to know?"
                  style={{ flex: 1, background: 'transparent', color: '#111827', border: 'none', outline: 'none', padding: '10px 8px' }}
                />
                <button onClick={startVoice} aria-label="voice" style={{ background: '#0ea5e9', border: 'none', color: 'white', padding: '10px 12px', borderRadius: 12 }}>{recording ? '🎙️...' : '🎙️'}</button>
              </div>

            </div>
          </div>
        ) : (
          <>
            <div ref={listRef} style={{ display: 'grid', gap: 6, padding: '100 8px', alignItems: 'start', gridAutoRows: 'max-content', margin: 0 }}>
              {messages.map((m) => (
                <div key={m.id} style={{ display: 'grid', gap: 6 }}>
                  <MessageBubble role={m.role} media={m.media}>{m.text}</MessageBubble>
                  {m.role === 'assistant' && (
                    <div style={{ display: 'flex', gap: 8 }}>
                      <button aria-label="like" style={{ background: 'transparent', border: '1px solid #e5e7eb', color: '#374151', padding: '4px 8px', borderRadius: 8 }}>👍</button>
                      <button aria-label="unlike" style={{ background: 'transparent', border: '1px solid #e5e7eb', color: '#374151', padding: '4px 8px', borderRadius: 8 }}>👎</button>
                    </div>
                  )}
                </div>
              ))}
              <div ref={bottomRef} />
            </div>
            <div style={{ position: 'fixed', left: 0, right: 0, bottom: 0 }}>
              <div style={{ maxWidth: 980, margin: '0 auto', position: 'relative' }}>
              {mediaPreview && (
                <img src={mediaPreview} alt="preview" style={{ position: 'absolute', bottom: '100%', left: 0, width: 56, height: 56, objectFit: 'cover', borderRadius: 12, boxShadow: '0 6px 20px rgba(0,0,0,0.12)', marginBottom: 8 }} />
              )}
              <div style={{ display: 'flex', alignItems: 'center', gap: 8, background: '#ffffff', border: '1px solid #e5e7eb', borderRadius: 16, padding: 8 }}>
                <label style={{ cursor: 'pointer', padding: 8 }}>
                  📎
                  <input accept="*/*" type="file" style={{ display: 'none' }} onChange={onPickMedia} />
                </label>
                <input
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  onKeyDown={(e) => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); sendMessage() } }}
                  placeholder="What do you want to know?"
                  style={{ flex: 1, background: 'transparent', color: '#111827', border: 'none', outline: 'none', padding: '10px 8px' }}
                />
                <button onClick={startVoice} aria-label="voice" style={{ background: '#0ea5e9', border: 'none', color: 'white', padding: '10px 12px', borderRadius: 12 }}>{recording ? '🎙️...' : '🎙️'}</button>
              </div>

              </div>
            </div>
          </>
        )}
      </div>
    </main>
  )
}

export default ChatPage
