import React, { useEffect, useMemo, useRef, useState } from 'react'
import { Helmet } from 'react-helmet'
import { Link } from 'react-router-dom'

const Container = ({ children }) => (
  <main style={{ maxWidth: 1152, margin: '0 auto', padding: '0 16px' }}>{children}</main>
)

const Sticky = ({ children }) => (
  <div style={{ position: 'sticky', top: 72 }}>{children}</div>
)

const SectionCard = ({ children }) => (
  <section style={{ background: 'rgba(255,255,255,0.8)', border: '1px solid #e5e7eb', borderRadius: 16, padding: 16 }}>{children}</section>
)

const FollowingList = ({ people }) => (
  <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
    {people.map((p) => (
      <li key={p.id} style={{ display: 'flex', alignItems: 'center', padding: '10px 0' }}>
        <img src={p.avatarUrl} alt={p.name} width={28} height={28} style={{ borderRadius: 14, marginRight: 8 }} />
        <span style={{ flex: 1 }}>{p.name}</span>
        {p.isActive && <span style={{ width: 8, height: 8, borderRadius: 4, background: '#16a34a' }} />}
      </li>
    ))}
  </ul>
)

const Tabs = ({ tabs, active, onChange }) => (
  <div style={{ display: 'flex', gap: 16, borderBottom: '1px solid #eee', marginBottom: 12 }}>
    {tabs.map((t) => (
      <button key={t} onClick={() => onChange(t)} style={{ padding: '10px 0', border: 'none', borderBottom: active === t ? '2px solid #111' : '2px solid transparent', background: 'transparent', fontWeight: 700, cursor: 'pointer' }}>{t}</button>
    ))}
  </div>
)

const PostCard = ({ item }) => (
  <article style={{ display: 'grid', gridTemplateColumns: '48px 1fr', gap: 12, padding: 16, border: '1px solid #e5e7eb', borderRadius: 16, background: 'rgba(255,255,255,0.8)' }}>
    <img src={item.author?.avatarUrl} alt={item.author?.name} width={48} height={48} style={{ borderRadius: 24 }} />
    <div>
      <div style={{ display: 'flex', alignItems: 'baseline', gap: 8 }}>
        <strong>{item.author?.name}</strong>
        <span style={{ color: '#6b7280', fontSize: 13 }}>{item.dateISO} ¬∑ {item.readTimeMin ?? 5} min</span>
      </div>
      <h3 style={{ margin: '4px 0 8px 0', fontSize: 18 }}>
        <Link to={`/blogs/${item.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>{item.title}</Link>
      </h3>
      {item.subtitle && <p style={{ margin: 0, color: '#6b7280' }}>{item.subtitle}</p>}
      <div style={{ display: 'flex', gap: 16, marginTop: 10, color: '#6b7280', fontSize: 14 }}>
        <span>üí¨ {item.stats?.responses ?? 0}</span>
        <span>üîñ {(item.stats?.bookmarks ?? 0)}</span>
        <span>üëÅÔ∏è {item.stats?.views ?? 0}</span>
      </div>
    </div>
  </article>
)

const Skeleton = () => (
  <div style={{ display: 'grid', gap: 12 }}>
    {[0,1,2].map((k) => (
      <div key={k} style={{ height: 120, borderRadius: 16, border: '1px solid #eee', background: 'linear-gradient(90deg,#f3f4f6 25%,#e5e7eb 37%,#f3f4f6 63%)', backgroundSize: '400% 100%', animation: 'pulse 1.4s ease infinite' }} />
    ))}
  </div>
)

const useFetch = (url) => {
  const [data, setData] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  useEffect(() => {
    let mounted = true
    setLoading(true)
    fetch(url)
      .then((r) => r.json())
      .then((j) => mounted && (setData(j), setLoading(false)))
      .catch((e) => mounted && (setError(e), setLoading(false)))
    return () => { mounted = false }
  }, [url])
  return { data, loading, error }
}

const useInfiniteBlogs = () => {
  const [items, setItems] = useState([])
  const [cursor, setCursor] = useState(null)
  const [loading, setLoading] = useState(false)
  const [done, setDone] = useState(false)
  const load = () => {
    if (loading || done) return
    setLoading(true)
    const qs = new URLSearchParams({ limit: '10' })
    if (cursor) qs.set('cursor', cursor)
    fetch(`/api/v1/blogs?${qs.toString()}`)
      .then((r) => r.json())
      .then((j) => {
        setItems((prev) => prev.concat(j.items || []))
        if (j.nextCursor) setCursor(j.nextCursor)
        else setDone(true)
      })
      .finally(() => setLoading(false))
  }
  useEffect(() => { load() }, [])
  return { items, loading, done, load }
}

const CommunityPage = () => {
  const [tab, setTab] = useState('For you') // For you | Following
  const blogs = useInfiniteBlogs()
  const { data: following } = useFetch('/api/v1/followings')
  const { data: staff } = useFetch('/api/v1/staff-picks')
  const sentinelRef = useRef(null)

  // infinite scroll
  useEffect(() => {
    const el = sentinelRef.current
    if (!el) return
    const io = new IntersectionObserver((entries) => {
      entries.forEach((e) => { if (e.isIntersecting) blogs.load() })
    })
    io.observe(el)
    return () => io.disconnect()
  }, [sentinelRef.current, blogs])

  const followingIds = useMemo(() => new Set((following?.items || []).map((u) => u.id)), [following])
  const visible = useMemo(() => {
    if (tab === 'For you') return blogs.items
    return blogs.items.filter((p) => followingIds.has(p.author?.id))
  }, [tab, blogs.items, followingIds])

  // responsive columns
  const [width, setWidth] = useState(typeof window !== 'undefined' ? window.innerWidth : 1024)
  useEffect(() => {
    const onResize = () => setWidth(window.innerWidth)
    window.addEventListener('resize', onResize)
    return () => window.removeEventListener('resize', onResize)
  }, [])
  const showLeft = width >= 1024
  const showRight = width >= 1280
  const gridStyle = useMemo(() => {
    if (showLeft && showRight) return { display: 'grid', gap: 24, gridTemplateColumns: '240px 1fr 320px' }
    if (showLeft && !showRight) return { display: 'grid', gap: 24, gridTemplateColumns: '240px 1fr' }
    return { display: 'grid', gap: 24, gridTemplateColumns: '1fr' }
  }, [showLeft, showRight])

  return (
    <Container>
      <Helmet>
        <title>Community</title>
      </Helmet>
      <div style={gridStyle}>
        {showLeft && (
          <aside>
            <Sticky>
              <SectionCard>
                <h4 style={{ margin: '0 0 12px 0' }}>Following</h4>
                {following?.items?.length ? (
                  <div style={{ maxHeight: '60vh', overflowY: 'auto', paddingRight: 4 }}>
                    <FollowingList people={following.items} />
                  </div>
                ) : (
                  <div style={{ color: '#6b7280' }}>No following.</div>
                )}
              </SectionCard>
            </Sticky>
          </aside>
        )}

        <section>
          <Tabs tabs={[ 'For you', 'Following' ]} active={tab} onChange={setTab} />
          <div style={{ display: 'grid', gap: 12 }}>
            {visible.map((it) => (
              <PostCard key={it.id} item={it} />
            ))}
            {!blogs.items.length && <Skeleton />}
          </div>
          <div ref={sentinelRef} style={{ height: 1 }} />
        </section>

        {showRight && (
          <aside>
            <Sticky>
              <SectionCard>
                <h4 style={{ marginTop: 0 }}>Staff Picks</h4>
                <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                  {staff?.items?.length ? staff.items.map((sp) => (
                    <li key={sp.id} style={{ padding: '8px 0' }}>
                      <Link to={`/blogs/${sp.id}`} style={{ textDecoration: 'none', color: '#111' }}>{sp.title}</Link>
                      <div style={{ color: '#6b7280', fontSize: 13 }}>{sp.author?.name} ¬∑ {sp.dateISO}</div>
                    </li>
                  )) : <div style={{ color: '#6b7280' }}>No staff picks.</div>}
                </ul>
              </SectionCard>
            </Sticky>
          </aside>
        )}
      </div>
    </Container>
  )
}

export default CommunityPage
